import React  from "react";
import { Component } from 'react';
import './githubuser.styles.css';

class GitHubUser extends Component{

    constructor(){
     super();   
     this.state={
        user:null,
        username:'shivanigiri',
        error:null,
        users:[]
        // loading:false
     };
    }

    componentDidMount(){
        this.fetchUser(this.state.username);
      }

    // fetchUser= (username)=>{
    //     console.log('https://api.github.com/users/${username}');
    //     fetch('https://api.github.com/users/${username}')
    //       .then(response=>{
    //         if(!response.ok){
    //           throw new Error('Network issue while receiving response.');
    //         }
    //         return response.json();
    //       })
    //       .then(data=> this.setState({user:data}))
    //       .catch(error => {
    //         this.setState({ error: 'Error fetching user data'});
    //       });
    //   }

    fetchUser = (username) => {
        fetch(`https://api.github.com/users/${username}`)
          .then(response => {
            if (!response.ok) {
              throw new Error('Network issue while receiving response.');
            }
            return response.json();
          })
          .then(data => this.setState({ user: data, error: null }))
          .catch(error => {
            this.setState({ error: 'Error fetching user data' });
          });
      };


    handleInputChange = (event) => {
        this.setState({ username: event.target.value });
      };

      handleSearch = () => {
        // this.setState({ loading: true, error: null });
        this.fetchUser(this.state.username);
      };

      render(){
        const { user, error, username } = this.state;
        console.log(user);
        return(
            <div className="App">
            <h1>GitHub User Finder</h1>
              <input
                type="text" 
                value={username} 
                onChange={this.handleInputChange} 
                placeholder="Enter GitHub username" 
              />
              {/* <button type="submit"><i class="fa fa-search"></i></button> */}
              <button className="search-field" onClick={this.handleSearch} ><i class="fa fa-search"></i></button>
              <br/>
              <br/>
            {/* <GitHubUser user={this.state.user}/> */}
            <p>{error}</p>
            {(user != null) ? (
                 <div className="github-result">

               </div>
            ):(<div>User is null</div>)
            }

          </div>

        );
      };
}


export  {GitHubUser};
