import React  from "react";
import { Component } from 'react';
import './githubuser.styles.css';
import { UserForm } from "./form/user-form.component";

class GitHubUser extends Component{

    constructor(){
     super();   
     this.state={
        user:null,
        username:'shivanigiri',
        error:null
        // loading:false
     };
    }

    componentDidMount(){
        this.fetchUser(this.state.username);
      }


    fetchUser = (username) => {
        fetch(`https://api.github.com/users/${username}`)
          .then(response => {
            if (!response.ok) {
              throw new Error('Network issue while receiving response.');
            }
            return response.json();
          })
          .then(data => this.setState({ user: data, error: null }))
          .catch(error => {
            this.setState({ error: 'Error fetching user data' });
          });
      };


    handleInputChange = (event) => {
        this.setState({ username: event.target.value });
      };

      handleSearch = () => {
        this.setState({ loading: true, error: null });
        this.fetchUser(this.state.username);
      };

      handleKeyDown = (e) => {
        if(e,key === 'Enter'){
            this.handleSearch();
        }
      };

    // handleKeyDown = (event) => {
    //     if (event.key === 'Enter') {
    //       this.handleSearch();
    //     }
    //   };


      render(){
        const { user, error, username } = this.state;
        console.log(user);
        return(
            <div className="App">
            {/* <img src="https://t4.ftcdn.net/jpg/06/52/16/27/360_F_652162792_yVVz1nKrdLxUvemAJUe4RfBHAXd3MgCI.jpg"alt="icon ani." width="150" className="animated-icon" /> */}
            <h1 className="title">Github Haystack</h1>
              <input
                type="text" 
                value={username} 
                onChange={this.handleInputChange} 
                // onKeyDown={this.handleKeyDown}
                onKeyDown={this.handleKeyDown}
                placeholder="Enter GitHub username" 
              />
              {/* <button type="submit"><i class="fa fa-search"></i></button> */}
              <button className="search-field" onClick={this.handleSearch} >Search</button>
              <br/>
              <br/>
            {/* <GitHubUser user={this.state.user}/> */}
            <p>{error}</p>
            {(user != null) ? (
                 <div className="github-result">
                 <img src={user.avatar_url} alt={user.login} width="150" />
                 <h2>{user.name || user.login}</h2>
                 <p>{user.bio}</p>
                 <table className="user-details">
                    <tr >
                        <th>
                            Followers: 
                        </th>
                        <th>
                            Following: 
                        </th>
                        <th>
                            Public Repos: 
                        </th>
                    </tr>
                    <tr>
                        <td>
                            {user.followers}
                        </td>
                        <td>
                            {user.following}
                        </td>
                        <td>
                            {user.public_repos}
                        </td>
                    </tr>
                 </table>
                <br/><br/>            
                 <a className="github-link" href={user.html_url} target="_blank" rel="noopener noreferrer">
                   Visit GitHub Profile
                 </a>
               </div>
            ):(<div>User is null</div>)
            }
           <UserForm/> 
           {/* <UserForm /> */}
          </div>

        );
      };
}


export  {GitHubUser};
