import React  from "react";
import { Component } from 'react';

class GitHubUser extends Component{

    constructor(){
     super();   
     this.state={
        user:null,
        username:'rabindra12345',
        error:null
        // loading:false
     };
    }

    componentDidMount(){
        this.fetchUser(this.state.username);
      }

    fetchUser= (username)=>{
        console.log('https://api.github.com/users/${username}');
        fetch('https://api.github.com/users/${username}')
          .then(response=>{
            if(!response.ok){
              throw new Error('Network issue while receiving response.');
            }
            return response.json();
          })
          .then(data=> this.setState({user:data,error:null}))
          .catch(error => {
            this.setState({ error: 'Error fetching user data'});
          });
      };

    // fetchUser = (username) => {
    //     fetch(`https://api.github.com/users/${username}`)
    //       .then(response => {
    //         if (!response.ok) {
    //           throw new Error('Network issue while receiving response.');
    //         }
    //         return response.json();
    //       })
    //       .then(data => this.setState({ user: data, error: null }))
    //       .catch(error => {
    //         this.setState({ error: 'Error fetching user data' });
    //       });
    //   };

    handleInputChange = (event) => {
        this.setState({ username: event.target.value });
      };

      handleSearch = () => {
        // this.setState({ loading: true, error: null });
        this.fetchUser(this.state.username);
      };

      render(){
        const { user, error, username } = this.state;
        console.log(user);
        return(
            <div className="App">
            <h1>GitHub User Finder</h1>
              <input 
                type="text" 
                value={username} 
                onChange={this.handleInputChange} 
                placeholder="Enter GitHub username" 
              />
              <button onClick={this.handleSearch}>Search</button>
              <br/>
              <br/>
            {/* <GitHubUser user={this.state.user}/> */}
            <p>{error}</p>
            {(user != null) ? (
                 <div>
                 <img src={user.avatar_url} alt={user.login} width="150" />
                 <h2>{user.name || user.login}</h2>
                 <p>{user.bio}</p>
                 <p>Followers: {user.followers}</p>
                 <p>Following: {user.following}</p>
                 <p>Public Repos: {user.public_repos}</p>
                 <a href={user.html_url} target="_blank" rel="noopener noreferrer">
                   Visit GitHub Profile
                 </a>
               </div>
            ):(<div>User is null</div>)
            }

          </div>

        );
      };
}


export  {GitHubUser};
