import React  from "react";
import { Component } from 'react';
import './githubuser.styles.css';
import {UserForm} from './form/user-form.component.jsx';

class GitHubUser extends Component {
  constructor() {
    super();
    this.state = {
      user: null,
      username: 'shivanigiri',
      error: null,
      loading: false,
    };
  }

  componentDidMount() {
    this.fetchUser(this.state.username);
  }

  fetchUser = (username) => {
    this.setState({ loading: true, error: null, user: null });
    fetch(`https://api.github.com/users/${username}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network issue while receiving response.');
        }
        return response.json();
      })
      .then((data) => this.setState({ user: data, error: null, loading: false }))
      .catch((error) => {
        this.setState({ error: 'Error fetching user data', user: null, loading: false });
      });
  };

  handleInputChange = (event) => {
    this.setState({ username: event.target.value });
  };

  handleSearch = () => {
    this.setState({ loading: true, error: null });
    this.fetchUser(this.state.username);
  };

  render() {
    const { user, error, username, loading } = this.state;
    // console.log(user);
    return (
      <div className="App">
        <h1 className="title">Github Haystack</h1>
        <input
          type="text"
          value={username}
          onChange={this.handleInputChange}
          placeholder="Enter GitHub username"
        />
        <button className="search-field" onClick={this.handleSearch}>
          Search
        </button>
        <br />
        <br />
        {loading && <p>Loading...</p>}
        {error && <p>{error}</p>}
        {(user!=null) ? (
          <div className="github-result">
            <img src={user.avatar_url} alt={user.login} width="150" />
            <h2>{user.name || user.login}</h2>
            <p>{user.bio}</p>
            <table className="user-details">
              <thead>
                <tr>
                  <th>Followers:</th>
                  <th>Following:</th>
                  <th>Public Repos:</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{user.followers}</td>
                  <td>{user.following}</td>
                  <td>{user.public_repos}</td>
                </tr>
              </tbody>
            </table>
            <br />
            <br />
            <a className="github-link" href={user.html_url} target="_blank" rel="noopener noreferrer">
              Visit GitHub Profile
            </a>
          </div>
        ) : (
          !loading && !error && <div>User is null</div>
        )}
        <br />
        <br />
        <UserForm />
      </div>
    );
  }
}

export { GitHubUser };
