import React  from "react";
import { Component } from 'react';
import './githubuser.styles.css';

class GitHubUser extends Component{

    constructor(){
     super();   
     this.state={
        user:null,
        username:'shivani',
        error:null,
        users:[]
        // loading:false
     };
    }

    componentDidMount(){
        this.fetchUser(this.state.username);
        fetch(`https://api.github.com/users/${username}*`)
          .then(response => {
            if (!response.ok) {
              throw new Error('Network issue while receiving response.');
            }
            return response.json();
          })
          .then(data => this.setState({ users: data, error: null }))
          .catch(error => {
            this.setState({ error: 'Error fetching user data' });
          });
      }

    // fetchUser= (username)=>{
    //     console.log('https://api.github.com/users/${username}');
    //     fetch('https://api.github.com/users/${username}')
    //       .then(response=>{
    //         if(!response.ok){
    //           throw new Error('Network issue while receiving response.');
    //         }
    //         return response.json();
    //       })
    //       .then(data=> this.setState({user:data}))
    //       .catch(error => {
    //         this.setState({ error: 'Error fetching user data'});
    //       });
    //   }

    // fetchUser = (username) => {
    //     fetch(`https://api.github.com/users/${username}*`)
    //       .then(response => {
    //         if (!response.ok) {
    //           throw new Error('Network issue while receiving response.');
    //         }
    //         return response.json();
    //       })
    //       .then(data => this.setState({ users: data, error: null }))
    //       .catch(error => {
    //         this.setState({ error: 'Error fetching user data' });
    //       });
    //   };


    handleInputChange = (event) => {
        this.setState({ username: event.target.value });
      };

      handleSearch = () => {
        // this.setState({ loading: true, error: null });
        this.fetchUser(this.state.username);
      };

      render(){
        const { user, error, username,users } = this.state;
        console.log(user);
        return(
            <div className="App">
            <h1>GitHub User Finder</h1>
              <input
                type="text" 
                value={username} 
                // onChange={this.handleInputChange} 
                placeholder="Enter GitHub username" 
              />
              {/* <button type="submit"><i class="fa fa-search"></i></button> */}
              <button className="search-field" onClick={this.handleSearch} >Search</button>
              <br/>
              <br/>
            {/* <GitHubUser user={this.state.user}/> */}
            <p>{error}</p>
            <div className="github-result">
                {users.map(user => (
                    <div key={user.login}>
                        <img src={user.avatar_url} alt={user.login} width="150" />
                        <h2>{user.login}</h2>
                        <a href={user.html_url} target="_blank" rel="noopener noreferrer">
                            Visit GitHub Profile
                        </a>
                    </div>
                    ))}
            </div>
          </div>

        );
      };
}


export  {GitHubUser};
