import React  from "react";
import { Component } from 'react';
import './githubuser.styles.css';

class GitHubUser extends Component{

    constructor(){
     super();   
     this.state={
        user:null,
        username:'shivanigiri',
        error:null
        // loading:false
     };
    }

    componentDidMount(){
        this.fetchUser(this.state.username);
      }


    fetchUser = (username) => {
        fetch(`https://api.github.com/users/${username}`)
          .then(response => {
            if (!response.ok) {
              throw new Error('Network issue while receiving response.');
            }
            return response.json();
          })
          .then(data => this.setState({ user: data, error: null }))
          .catch(error => {
            this.setState({ error: 'Error fetching user data' });
          });
      };


    handleInputChange = (event) => {
        this.setState({ username: event.target.value });
      };

      handleSearch = () => {
        // this.setState({ loading: true, error: null });
        this.fetchUser(this.state.username);
      };

      render(){
        const { user, error, username } = this.state;
        console.log(user);
        return(
            <div className="App">
            <h1>GitHub User Finder</h1>
              <input
                type="text" 
                value={username} 
                onChange={this.handleInputChange} 
                placeholder="Enter GitHub username" 
              />
              {/* <button type="submit"><i class="fa fa-search"></i></button> */}
              <button className="search-field" onClick={this.handleSearch} ><i class="fa fa-search"></i></button>
              <br/>
              <br/>
            {/* <GitHubUser user={this.state.user}/> */}
            <p>{error}</p>
            {(user != null) ? (
                 <div className="github-result">
                 <img src={user.avatar_url} alt={user.login} width="150" />
                 <h2>{user.name || user.login}</h2>
                 <p>{user.bio}</p>
                 <table className="user-details">
                    <tr >
                        <th>
                            Followers: 
                        </th>
                        <th>
                            Following: 
                        </th>
                        <th>
                            Public Repos: 
                        </th>
                    </tr>
                    <tr>
                        <td>
                            {user.followers}
                        </td>
                        <td>
                            {user.following}
                        </td>
                        <td>
                            {user.public_repos}
                        </td>
                    </tr>
                 </table>
                 <table id="customers">
  <tr>
    <th>Company</th>
    <th>Contact</th>
    <th>Country</th>
  </tr>
  <tr>
    <td>Alfreds Futterkiste</td>
    <td>Maria Anders</td>
    <td>Germany</td>
  </tr>
  <tr>
    <td>Berglunds snabbköp</td>
    <td>Christina Berglund</td>
    <td>Sweden</td>
  </tr>
  <tr>
    <td>Centro comercial Moctezuma</td>
    <td>Francisco Chang</td>
    <td>Mexico</td>
  </tr>
  <tr>
    <td>Ernst Handel</td>
    <td>Roland Mendel</td>
    <td>Austria</td>
  </tr>
  <tr>
    <td>Island Trading</td>
    <td>Helen Bennett</td>
    <td>UK</td>
  </tr>
  <tr>
    <td>Königlich Essen</td>
    <td>Philip Cramer</td>
    <td>Germany</td>
  </tr>
  <tr>
    <td>Laughing Bacchus Winecellars</td>
    <td>Yoshi Tannamuri</td>
    <td>Canada</td>
  </tr>
  <tr>
    <td>Magazzini Alimentari Riuniti</td>
    <td>Giovanni Rovelli</td>
    <td>Italy</td>
  </tr>
  <tr>
    <td>North/South</td>
    <td>Simon Crowther</td>
    <td>UK</td>
  </tr>
  <tr>
    <td>Paris spécialités</td>
    <td>Marie Bertrand</td>
    <td>France</td>
  </tr>
</table>
                 <a href={user.html_url} target="_blank" rel="noopener noreferrer">
                   Visit GitHub Profile
                 </a>
               </div>
            ):(<div>User is null</div>)
            }

          </div>

        );
      };
}


export  {GitHubUser};
