// src/GitHubUser.js
import React, { Component } from 'react';

class GitHubUser extends Component {
  state = {
    username: 'octocat', // default username
    user: null,
    loading: true,
    error: null,
  };

  componentDidMount() {
    this.fetchUser(this.state.username); // Fetch user data with the default username
  }

  fetchUser = (username) => {
    fetch(`https://api.github.com/users/${username}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        this.setState({ user: data, loading: false });
      })
      .catch(error => {
        this.setState({ error: 'Error fetching user data', loading: false });
      });
  };

  handleInputChange = (event) => {
    this.setState({ username: event.target.value });
  };

  handleSearch = () => {
    this.setState({ loading: true, error: null });
    this.fetchUser(this.state.username);
  };

  render() {
    const { user, loading, error, username } = this.state;

    return (
      <div>
        <h1>GitHub User Finder</h1>
        <input 
          type="text" 
          value={username} 
          onChange={this.handleInputChange} 
          placeholder="Enter GitHub username" 
        />
        <button onClick={this.handleSearch}>Search</button>

        {loading ? (
          <p>Loading...</p>
        ) : error ? (
          <p>{error}</p>
        ) : user ? (
          <div>
            <img src={user.avatar_url} alt={user.login} width="150" />
            <h2>{user.name || user.login}</h2>
            <p>{user.bio}</p>
            <p>Followers: {user.followers}</p>
            <p>Following: {user.following}</p>
            <p>Public Repos: {user.public_repos}</p>
            <a href={user.html_url} target="_blank" rel="noopener noreferrer">
              Visit GitHub Profile
            </a>
          </div>
        ) : null}
      </div>
    );
  }
}

export default GitHubUser;
